CREATE OR REPLACE FUNCTION obter_per_vidas_conv (cd_convenio_p bigint, dt_referencia_p timestamp, qt_atendimento_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* IE_OPCAO_P
	'E' - dentro do estado
	'T' - total operadora
*/
pr_vidas_w		double precision := 0;
nr_vidas_estado_w	bigint;
nr_vidas_operadora_w	bigint;


BEGIN

select	coalesce(max(a.nr_vidas_estado), 0),
	coalesce(max(a.nr_vidas_operadora), 0)
into STRICT	nr_vidas_estado_w,
	nr_vidas_operadora_w
from	convenio_operadora a
where	a.cd_convenio	= cd_convenio_p
and	a.dt_vigencia	=
	(SELECT	max(x.dt_vigencia)
	from	convenio_operadora x
	where	x.cd_convenio	= a.cd_convenio
	and	x.dt_vigencia	<= dt_referencia_p);

if (ie_opcao_p = 'E') then
	pr_vidas_w	:= dividir((qt_atendimento_p * 100), nr_vidas_estado_w);
else
	pr_vidas_w	:= dividir((qt_atendimento_p * 100), nr_vidas_operadora_w);
end if;

return	pr_vidas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
