        ELSE
            qt_peso_ajustado_w := ( ( coalesce(qt_peso_atual_p,0) - coalesce(qt_peso_habit_p,0) ) * 0.25 ) + coalesce(qt_peso_habit_p,0);
        END IF;

    ELSE
        qt_peso_ajustado_w := 0;
    END IF;

    RETURN qt_peso_ajustado_w;
END;
