CREATE OR REPLACE FUNCTION obter_peso_altura_agenda ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_retorno_w	double precision;
qt_peso_w	real;
qt_altura_cm_w	real;

/*
P - Peso
A - Altura
*/
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	select	qt_peso,
		qt_altura_cm
	into STRICT	qt_peso_w,
		qt_altura_cm_w
	from	agenda_paciente
	where	nr_sequencia = nr_sequencia_p;

	if (ie_opcao_p = 'P') then
		qt_retorno_w	:= qt_peso_w;
	elsif (ie_opcao_p = 'A') then
		qt_retorno_w	:= qt_altura_cm_w;
	end if;
	end;
end if;

RETURN qt_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
