CREATE OR REPLACE FUNCTION obter_peso_altura_prescr (nr_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

qt_peso_w	double precision;
qt_altura_w	real;
ds_retorno_w	varchar(50);


BEGIN

SELECT qt_altura_cm,
qt_peso
into STRICT qt_altura_w,
qt_peso_w
FROM prescr_medica where nr_prescricao = nr_prescricao_p;

if (ie_opcao_p = 'A') then
	ds_retorno_w := qt_altura_w;
elsif (ie_opcao_p = 'P') then
	ds_retorno_w := qt_peso_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
