CREATE OR REPLACE FUNCTION obter_peso_atend_prescr ( nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_prescricao_w	bigint;
qt_peso_w	real;
ds_retorno_W	varchar(150);


BEGIN

select	max(nr_prescricao)
into STRICT	nr_prescricao_w
from	prescr_medica
where	nr_atendimento	=	nr_atendimento_p
and	(qt_peso IS NOT NULL AND qt_peso::text <> '');

