CREATE OR REPLACE FUNCTION obter_peso_atual_ciclo (nr_seq_ciclo_p bigint, ie_opcao_p text, cd_setor_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_conjunto_w	cm_conjunto_cont.nr_seq_conjunto%type;
qt_peso_total_w	        cm_item.qt_peso%type := 0;
qt_peso_w	        cm_item.qt_peso%type := 0;		
		
C01 CURSOR FOR

SELECT 	nr_sequencia
from 	cm_conjunto_cont
where 	((ie_opcao_p = 'C' and nr_seq_ciclo = nr_seq_ciclo_p) or (ie_opcao_p = 'L' and cd_setor_atendimento = cd_setor_atendimento_p and nr_seq_ciclo_lav = nr_seq_ciclo_p));			


C02 CURSOR FOR

	SELECT 	nr_seq_item
	from 	cm_item_cont
	where 	nr_seq_conjunto = nr_seq_conjunto_w;
		
BEGIN

for c01_w in C01 loop
	
	nr_seq_conjunto_w := c01_w.nr_sequencia;
	
		for co2_w in C02 loop
		
			select qt_peso
			into STRICT qt_peso_w
			from cm_item
			where nr_sequencia = co2_w.nr_seq_item;
			
			qt_peso_total_w := qt_peso_total_w + coalesce(qt_peso_w,0);
			

		end loop;
	
end loop;

return	qt_peso_total_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
