CREATE OR REPLACE FUNCTION obter_peso_calorico_md (qt_peso_p bigint) RETURNS bigint AS $body$
DECLARE


qt_peso_calorico_w  double precision;


BEGIN

if (coalesce(qt_peso_p, 0) <= 10) then
  qt_peso_calorico_w  := dividir_md(100 * coalesce(qt_peso_p, 0),100);
elsif (coalesce(qt_peso_p, 0) <= 20) then
  qt_peso_calorico_w	:= dividir_md(1000 + ((coalesce(qt_peso_p, 0) - 10) * 50),100);
else
	qt_peso_calorico_w	:= dividir_md(1500 + ((coalesce(qt_peso_p, 0) - 20) * 20),100);
end if;

return	qt_peso_calorico_w;

end;
