SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_peso_ciclo (nr_seq_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

  qt_peso_number_w	double precision;
  qt_peso_w paciente_atendimento.qt_peso%type;
  value_w nls_session_parameters.value%type;


BEGIN

if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then
    select 	qt_peso
    into STRICT 	qt_peso_w
    from 	paciente_atendimento
    where 	nr_seq_atendimento = nr_seq_atendimento_p;

    select value
    into STRICT value_w
    from  nls_session_parameters
    where	parameter = 'NLS_NUMERIC_CHARACTERS';

    if (substr(value_w,1,1) = ',') then
      qt_peso_number_w := (replace(qt_peso_w,'.',','))::numeric;
    else
      qt_peso_number_w := (replace(qt_peso_w,',','.'))::numeric;
    end if;
end if;

return qt_peso_number_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
