CREATE OR REPLACE FUNCTION obter_peso_considerado_onc ( nr_sequencia_p bigint, nr_seq_material_p bigint, qt_peso_p bigint, qt_altura_cm_p bigint, ie_calc_ciclo_p text default 'S') RETURNS double precision AS $body$
DECLARE


peso_considerado_w 		double precision(10);
qt_peso_ideal_ajus_w    double precision(10);
qt_peso_ideal_w    double precision(10);
qt_fator_correcao_w     double precision;
ie_peso_considerar_w    varchar(1)  := 'A';
cd_pessoa_fisica_w 		varchar(20);
sql_w                   varchar(200);


BEGIN

if (ie_calc_ciclo_p = 'S') then
	select 	max(b.cd_pessoa_fisica)
	into STRICT 	cd_pessoa_fisica_w
	from 	paciente_atendimento a,
			paciente_setor b
	where 	a.nr_seq_paciente = b.nr_seq_paciente
	and 	a.nr_seq_atendimento = nr_sequencia_p;

	select 	max(qt_fator_correcao),
			max(ie_peso_considerar)
			into STRICT qt_fator_correcao_w,
			ie_peso_considerar_w
	from 	paciente_atend_medic
	where 	nr_seq_atendimento = nr_sequencia_p
	and		((coalesce(nr_seq_material_p::text, '') = '') or (nr_seq_material	= nr_seq_material_p));

else
	select 	max(b.cd_pessoa_fisica)
	into STRICT 	cd_pessoa_fisica_w
	from 	paciente_setor b
	where 	b.nr_seq_paciente = nr_sequencia_p;

	select 	max(qt_fator_correcao),
			max(ie_peso_considerar)
			into STRICT qt_fator_correcao_w,
			ie_peso_considerar_w
	from 	paciente_protocolo_medic
	where 	nr_seq_paciente = nr_sequencia_p
	and		((coalesce(nr_seq_material_p::text, '') = '') or (nr_seq_material	= nr_seq_material_p));

end if;




        SELECT
            obter_peso_ideal_ajust_pac(cd_pessoa_fisica_w, qt_peso_p, qt_fator_correcao_w, qt_altura_cm_p)
        INTO STRICT qt_peso_ideal_ajus_w
;

        SELECT
            obter_peso_ideal_pac(cd_pessoa_fisica_w, qt_altura_cm_p)
        INTO STRICT qt_peso_ideal_w
;

        BEGIN
            sql_w := 'CALL OBTER_PESO_CONSIDERADO_ONC_MD(:1, :2, :3, :4) INTO :peso_considerado_w';
            EXECUTE sql_w
                USING IN ie_peso_considerar_w, IN qt_peso_ideal_ajus_w, IN qt_peso_ideal_w, IN qt_peso_p, OUT peso_considerado_w;

        EXCEPTION
            WHEN OTHERS THEN
                peso_considerado_w := NULL;
        END;



return peso_considerado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
