CREATE OR REPLACE FUNCTION obter_peso_equip_ciclo (nr_seq_equipamento_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


retorno_w	double precision;
qt_peso_min_w   CM_EQUIPAMENTO.qt_peso_min%type;
qt_peso_max_w   CM_EQUIPAMENTO.qt_peso_max%type;
	
/*
MI - Peso mínimo
MA - Peso máximo
*/
		

BEGIN

if (nr_seq_equipamento_p IS NOT NULL AND nr_seq_equipamento_p::text <> '') then

	select 	qt_peso_min,
		qt_peso_max
	into STRICT 	qt_peso_min_w,
		qt_peso_max_w
	from 	cm_equipamento
	where 	nr_sequencia = nr_seq_equipamento_p;


if (ie_opcao_p = 'MI') then
	retorno_w := qt_peso_min_w;
else
	retorno_w := qt_peso_max_w;
end if;

end if;

return	coalesce(retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
