CREATE OR REPLACE FUNCTION obter_peso_ideal_pac_md ( qt_altura_cm_p bigint, ie_sexo_p text ) RETURNS double precision AS $body$
DECLARE

    qt_peso_ideal_w	double precision := 0.0;

BEGIN
	if (qt_altura_cm_p > 0) and (ie_sexo_p IS NOT NULL AND ie_sexo_p::text <> '') then
		if ('M' = ie_sexo_p) then
			select 	(50+(0.91)*(qt_altura_cm_p -152))
			into STRICT 	qt_peso_ideal_w
			;

		elsif ('F' = ie_sexo_p) then
			select 	(45+(0.91)*(qt_altura_cm_p -152))
			into STRICT 	qt_peso_ideal_w
			;
		end if;
	end if;
    return qt_peso_ideal_w;
end;
$body$
