CREATE OR REPLACE FUNCTION obter_peso_media_consumo ( dt_mesano_referencia_p timestamp, cd_material_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


cd_grupo_material_w		smallint;
cd_subgrupo_w			smallint;
cd_classe_material_w		integer;
qt_peso_w			double precision;
qt_retorno_w			double precision;
qt_existe_w			bigint;
dt_mesano_referencia_w	timestamp;

C01 CURSOR FOR
	SELECT	qt_peso
	from	sup_peso_media_consumo
	where	cd_estabelecimento					= cd_estabelecimento_p
	and	PKG_DATE_UTILS.start_of(dt_mesano_referencia, 'month', 0)		= dt_mesano_referencia_w
	and	coalesce(cd_grupo_material, cd_grupo_material_w)	= cd_grupo_material_w
	and	coalesce(cd_subgrupo_material, cd_subgrupo_w)		= cd_subgrupo_w
	and	coalesce(cd_classe_material, cd_classe_material_w)	= cd_classe_material_w
	and	coalesce(cd_material, cd_material_p)			= cd_material_p
	order by
		coalesce(cd_material, 0),
		coalesce(cd_classe_material, 0),
		coalesce(cd_subgrupo_material, 0),
		coalesce(cd_grupo_material, 0);


BEGIN
dt_mesano_referencia_w	:= PKG_DATE_UTILS.start_of(dt_mesano_referencia_p,'month',0);
qt_retorno_w	:= 1;

select	count(*)
into STRICT	qt_existe_w
from	sup_peso_media_consumo
where	cd_estabelecimento = cd_estabelecimento_p;
if (qt_existe_w > 0) then
	begin
	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_w,
		cd_classe_material_w
	from	estrutura_material_v
	where 	cd_material	= cd_material_p;

	open	c01;
	loop
	fetch	c01 into
		qt_peso_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		qt_retorno_w	:= qt_peso_w;
	end loop;
	close 	c01;
	end;
end if;

return qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
