CREATE OR REPLACE FUNCTION obter_pessoa_adiantamento ( nr_adiantamento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(200) := Null;


BEGIN 
 
begin 
select	nm_pessoa_adiant || ' - ' || 
		to_char(dt_adiantamento, 'dd/mm/yyyy') || ' - ' || 
		campo_mascara(vl_adiantamento, 2) 
into STRICT		ds_retorno_w 
from		adiantamento_v 
where		nr_adiantamento	= nr_adiantamento_p;
exception 
  		when others then 
		ds_retorno_w	:= Null;
end;
 
RETURN ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
