CREATE OR REPLACE FUNCTION obter_pessoa_assinatura ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_itens_w		bigint;
cd_medico_autenticacao_w	varchar(10);
cd_medico_w		varchar(10);
qt_conta_w		bigint := 0;

C01 CURSOR FOR
	SELECT	sum(qt_itens) qt_itens,
		cd_medico_autenticacao
	from (
		SELECT	count(*) qt_itens,
			cd_medico_autenticacao
		from	material_atend_paciente
		where	nr_atendimento = nr_atendimento_p
		and (cd_setor_atendimento = cd_setor_atendimento_p or cd_setor_atendimento_p = 0)
		and	(dt_autenticacao IS NOT NULL AND dt_autenticacao::text <> '')
		group by	cd_medico_autenticacao
		
union all

		select	count(*) qt_itens,
			cd_medico_autenticacao
		from	procedimento_paciente
		where	nr_atendimento = nr_atendimento_p
		and (cd_setor_atendimento = cd_setor_atendimento_p or cd_setor_atendimento_p = 0)
		and	(dt_autenticacao IS NOT NULL AND dt_autenticacao::text <> '')
		group by	cd_medico_autenticacao) alias7
	group by cd_medico_autenticacao;


BEGIN

open C01;
loop
fetch C01 into
	qt_itens_w,
	cd_medico_autenticacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (qt_itens_w > qt_conta_w) then
		cd_medico_w := cd_medico_autenticacao_w;
	end if;
	qt_conta_w := qt_itens_w;

	end;
end loop;
close C01;

return	cd_medico_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
