SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_pessoa_atendimento ( NR_ATENDIMENTO_P bigint, IE_CODIGO_NOME_P text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(200);
CD_PESSOA_FISICA_W	varchar(10);


BEGIN

IF (NR_ATENDIMENTO_P IS NOT NULL AND NR_ATENDIMENTO_P::text <> '') THEN
	BEGIN
	SELECT	max(CD_PESSOA_FISICA)
	INTO STRICT	CD_PESSOA_FISICA_W
	FROM	ATENDIMENTO_PACIENTE
	WHERE	NR_ATENDIMENTO = NR_ATENDIMENTO_P;

	IF (UPPER(IE_CODIGO_NOME_P) = 'C') THEN
		DS_RETORNO_W	:= CD_PESSOA_FISICA_W;
	ELSIF (UPPER(IE_CODIGO_NOME_P) = 'N') THEN
		DS_RETORNO_W	:= OBTER_NOME_PESSOA_FISICA(CD_PESSOA_FISICA_W, NULL);
	elsif (UPPER(IE_CODIGO_NOME_P) = 'NO') then
		DS_RETORNO_W	:=  Obter_Nome_PF_Oculta(CD_PESSOA_FISICA_W,obter_perfil_ativo,wheb_usuario_pck.get_nm_usuario);
	elsif (UPPER(IE_CODIGO_NOME_P) = 'NS') then

		DS_RETORNO_W	:=  OBTER_NOME_PESSOA_FISICA(CD_PESSOA_FISICA_W, NULL);

		if ((obter_dados_pf(CD_PESSOA_FISICA_W,'NSOC') IS NOT NULL AND (obter_dados_pf(CD_PESSOA_FISICA_W,'NSOC'))::text <> '')) then
			DS_RETORNO_W	:=  DS_RETORNO_W||' (' || Wheb_mensagem_pck.get_texto(455743) /* Nome Social */ || ': '||obter_dados_pf(CD_PESSOA_FISICA_W,'NSOC')||')';
		end if;

	END IF;
	END;
END IF;

RETURN DS_RETORNO_W;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
