CREATE OR REPLACE FUNCTION obter_pessoa_cobranca (cd_estabelecimento_p bigint, nr_seq_cobranca_p bigint, ie_tipo_pessoa_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_tipo_pessoa_p
PF	Pessoa Física
PF	Pessoa Jurídica
*/
nr_titulo_w		bigint;
nr_seq_cheque_w		bigint;
cd_pessoa_fisica_w	varchar(10);
cd_cgc_w		varchar(14);
ds_retorno_w		varchar(255);


BEGIN

select	max(nr_titulo),
	max(nr_seq_cheque)
into STRICT	nr_titulo_w,
	nr_seq_cheque_w
from	cobranca
where	nr_sequencia	= nr_seq_cobranca_p;

if (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then

	select	max(a.cd_pessoa_fisica),
		max(a.cd_cgc)
	into STRICT	cd_pessoa_fisica_w,
		cd_cgc_w
	from	titulo_receber a
	where	a.nr_titulo		= nr_titulo_w
	and (a.cd_estabelecimento 	= cd_estabelecimento_p or
		(obter_estab_financeiro(a.cd_estabelecimento))::numeric  = cd_estabelecimento_p);

elsif (nr_seq_cheque_w IS NOT NULL AND nr_seq_cheque_w::text <> '') then

	select	max(a.cd_pessoa_fisica),
		max(a.cd_cgc)
	into STRICT	cd_pessoa_fisica_w,
		cd_cgc_w
	from	cheque_cr a
	where	a.nr_seq_cheque		= nr_seq_cheque_w
	and (a.cd_estabelecimento 	= cd_estabelecimento_p or
		(obter_estab_financeiro(a.cd_estabelecimento))::numeric  = cd_estabelecimento_p);

end if;

if (ie_tipo_pessoa_p = 'PF') then
	ds_retorno_w	:= cd_pessoa_fisica_w;
elsif (ie_tipo_pessoa_p = 'PJ') then
	ds_retorno_w	:= cd_cgc_w;
end if;

return 	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
