CREATE OR REPLACE FUNCTION obter_pessoa_delegacao ( cd_pessoa_fisica_p text, ie_objetivo_p text, dt_limite_p timestamp) RETURNS varchar AS $body$
DECLARE


cd_pessoa_delegacao_w	varchar(10);
ds_retorno_w		varchar(10);


BEGIN

select	coalesce(max(cd_pessoa_substituta), 'X')
into STRICT	cd_pessoa_delegacao_w
from	pessoa_fisica_delegacao
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	ie_objetivo = ie_objetivo_p
and	((coalesce(trunc(dt_inicio_limite),trunc(clock_timestamp())) <= trunc(clock_timestamp())) and (trunc(dt_limite) >= trunc(clock_timestamp())));

if (cd_pessoa_delegacao_w <> 'X') then
	ds_retorno_w	:= cd_pessoa_delegacao_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
