CREATE OR REPLACE FUNCTION obter_pessoa_dest_emp (cd_pf_realizou_p san_producao.cd_pf_realizou%type) RETURNS varchar AS $body$
DECLARE


cd_pf_destino_w san_emprestimo.cd_pf_destino%type;


BEGIN

if (cd_pf_realizou_p IS NOT NULL AND cd_pf_realizou_p::text <> '') then

   select   max(a.cd_pf_destino)
   into STRICT     cd_pf_destino_w
   from     san_emprestimo a,
            san_producao b
   where    a.nr_sequencia      = b.nr_seq_emp_saida
   and      b.cd_pf_realizou    = cd_pf_realizou_p;

end if;

return cd_pf_destino_w;

end;
