CREATE OR REPLACE FUNCTION obter_pessoa_equipe_projeto ( nr_seq_projeto_p bigint, nr_seq_tipo_equipe_p bigint, nr_seq_tipo_funcao_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE

		 
ds_pessoa_equipe_w	varchar(255);
nr_seq_pessoa_w		bigint;
cd_pessoa_fisica_w	varchar(10);
		

BEGIN 
if (nr_seq_projeto_p IS NOT NULL AND nr_seq_projeto_p::text <> '') and (nr_seq_tipo_equipe_p IS NOT NULL AND nr_seq_tipo_equipe_p::text <> '') and (nr_seq_tipo_funcao_p IS NOT NULL AND nr_seq_tipo_funcao_p::text <> '') then 
	begin 
	select	coalesce(max(p.nr_sequencia),0) 
	into STRICT	nr_seq_pessoa_w 
	from	proj_equipe_papel p, 
		proj_equipe e 
	where	p.nr_seq_equipe = e.nr_sequencia 
	and	p.nr_seq_funcao = nr_seq_tipo_funcao_p 
	and	coalesce(p.ie_situacao,'A') = 'A' 
	and	e.nr_seq_equipe_funcao = nr_seq_tipo_equipe_p 
	and	e.nr_seq_proj = nr_seq_projeto_p;
	 
	if (nr_seq_pessoa_w > 0) then 
		begin 
		select	max(cd_pessoa_fisica) 
		into STRICT	cd_pessoa_fisica_w 
		from	proj_equipe_papel 
		where	nr_sequencia = nr_seq_pessoa_w;
		 
		if (ie_retorno_p = 'NM') then 
			begin 
			ds_pessoa_equipe_w := obter_nome_pf(cd_pessoa_fisica_w);
			end;
		elsif (ie_retorno_p = 'PNM') then 
			begin 
			ds_pessoa_equipe_w := obter_primeiro_nome(obter_nome_pf(cd_pessoa_fisica_w));
			end;
		else 
			begin 
			ds_pessoa_equipe_w := cd_pessoa_fisica_w;
			end;
		end if;
		end;
	end if;	
	end;
end if;
return ds_pessoa_equipe_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
