CREATE OR REPLACE FUNCTION obter_pessoa_escala_atend (nr_seq_escala_padrao_p bigint, cd_estabelecimento_p bigint, ie_tipo_atendimento_p bigint, ie_clinica_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(10);
qt_reg_w			bigint;
nr_seq_escala_w			bigint;

C01 CURSOR FOR
	SELECT	nr_seq_escala
	from	regra_escala_atend
	where	cd_estabelecimento				= cd_estabelecimento_p
	and	coalesce(ie_tipo_atendimento, ie_tipo_atendimento_p)	= ie_tipo_atendimento_p
	and	coalesce(ie_clinica, ie_clinica_p)			= ie_clinica_p
	order by 1;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from 	regra_escala_atend
where	cd_estabelecimento	= cd_estabelecimento_p;

nr_seq_escala_w		:= nr_seq_escala_padrao_p;

if (qt_reg_w	> 0) then
	begin

	open C01;
	loop
	fetch C01 into
		nr_seq_escala_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		nr_seq_escala_w	:= nr_seq_escala_w;
		end;
	end loop;
	close C01;

	if (coalesce(nr_seq_escala_w, 0) = 0) then
		nr_seq_escala_w		:= nr_seq_escala_padrao_p;
	end if;
	end;
end if;

if (coalesce(nr_seq_escala_w, 0) > 0) then
	select  obter_pessoa_fisica_escala(nr_seq_escala_w, dt_referencia_p)
	into STRICT	cd_pessoa_fisica_w
	;
end if;

return cd_pessoa_fisica_w;

end;
$body$
LANGUAGE PLPGSQL
