CREATE OR REPLACE FUNCTION obter_pessoa_fisica_loc_trab ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_setor_atendimento_w		varchar(255);
cd_setor_atendimento_w		double precision;


BEGIN

select	coalesce(max(cd_setor_atendimento),0)
into STRICT	cd_setor_atendimento_w
from 	PESSOA_FISICA_LOC_TRAB
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

if (cd_setor_atendimento_w = 0) then

	select	coalesce(max(a.cd_setor_atendimento),0)
	into STRICT	cd_setor_atendimento_w
	from	usuario a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p;

end if;

if (cd_setor_atendimento_w > 0) then
		select	max(ds_setor_atendimento)
		into STRICT	ds_setor_atendimento_w
		from	setor_atendimento
		where	cd_setor_atendimento	= cd_setor_atendimento_w;
end if;

return	ds_setor_atendimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
