CREATE OR REPLACE FUNCTION obter_pessoa_fisica_preceptor (cd_pessoa_estudante_p bigint, nr_seq_item_pront_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w bigint;


BEGIN

if (get_if_rule_preceptor(cd_pessoa_estudante_p) = 'S') then
	--person
	select	max(a.cd_pessoa_fisica)
	into STRICT 	vl_retorno_w
	from 	pessoa_fisica_preceptor a, pessoa_fisica_estudante b, liberacao_item_estudante c
	where 	a.cd_pessoa_estudante = cd_pessoa_estudante_p
	and	    b.cd_pessoa_fisica = c.cd_pessoa_estudante
	and	    c.nr_seq_item = nr_seq_item_pront_p
	and 	b.cd_pessoa_fisica = a.cd_pessoa_estudante
	and 	b.ie_situacao = 'A'
	and	    clock_timestamp() between coalesce(b.dt_inicio,clock_timestamp() - interval '1 days') and coalesce(b.dt_fim,clock_timestamp())
	and	    coalesce(c.cd_perfil, coalesce(obter_perfil_ativo,0))	= coalesce(obter_perfil_ativo,0);

else
	--profile
	select	max(c.cd_pessoa_fisica)
	into STRICT 	vl_retorno_w
	from	regra_preceptor_pep a, regra_preceptor_pep_item b, regra_preceptor_pep_prof c
	where	a.nr_sequencia = b.nr_seq_regra
	and		a.nr_sequencia = c.nr_seq_regra
	and 	a.cd_perfil = coalesce(obter_perfil_ativo,0)
	and		b.nr_seq_item = nr_seq_item_pront_p
	and		clock_timestamp() between coalesce(a.dt_inicio,clock_timestamp()) and coalesce(a.dt_fim,clock_timestamp())
	and		coalesce(a.ie_situacao,'A') = 'A';
	
end if;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
