SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_pessoa_indicacao_atend ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

					 
ds_retorno_w		varchar(255) := null;
nr_sequencia_w		bigint;	
cd_pessoa_fisica_w	atendimento_paciente.cd_pessoa_indic%type;
cd_medico_w		atendimento_paciente.cd_pessoa_juridica_indic%type;
cd_cgc_indicacao_w	atendimento_paciente.cd_cgc_indicacao%type;	
cd_tipo_midia_w		atendimento_paciente.nr_seq_tipo_midia%type;	
 

BEGIN 
 
if (coalesce(nr_atendimento_p,0) > 0) then 
	select 	max(a.nr_sequencia) 
	into STRICT	nr_sequencia_w 
	from	atendimento_indicacao a 
	where	a.nr_atendimento = nr_atendimento_p 
	and	a.dt_atualizacao_nrec = (SELECT	max(y.dt_atualizacao_nrec) 
					 from	atendimento_paciente x, 
						atendimento_indicacao y 
					 where	y.nr_seq_indicacao = x.nr_seq_indicacao 
					 and	x.nr_atendimento  = a.nr_atendimento);
 
	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then 
		select	max(cd_pessoa_fisica), 
			max(cd_medico), 
			max(cd_cgc_indicacao) 
		into STRICT	cd_pessoa_fisica_w, 
			cd_medico_w, 
			cd_cgc_indicacao_w 
		from 	atendimento_indicacao 
		where	nr_sequencia = nr_sequencia_w;
		 
		if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
			ds_retorno_w := substr(obter_nome_pf(cd_pessoa_fisica_w),1,254);
		elsif (cd_medico_w IS NOT NULL AND cd_medico_w::text <> '') then 
			ds_retorno_w := substr(obter_nome_pf(cd_medico_w),1,254);
		elsif (cd_cgc_indicacao_w IS NOT NULL AND cd_cgc_indicacao_w::text <> '') then 
			ds_retorno_w := substr(obter_nome_pf_pj(null, cd_cgc_indicacao_w),1,254);
		end if;
	else 
	 
		select	max(cd_pessoa_indic), 
			max(cd_pessoa_juridica_indic), 
			max(cd_cgc_indicacao), 
			max(nr_seq_tipo_midia) 
		into STRICT	cd_pessoa_fisica_w, 
			cd_medico_w, 
			cd_cgc_indicacao_w, 
			cd_tipo_midia_w		 
		from 	atendimento_paciente a 
		where	a.nr_atendimento = nr_atendimento_p 
		and	(a.nr_seq_indicacao IS NOT NULL AND a.nr_seq_indicacao::text <> '');
		 
		if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
			ds_retorno_w := substr(obter_nome_pf(cd_pessoa_fisica_w),1,254);
		elsif (cd_medico_w IS NOT NULL AND cd_medico_w::text <> '') then 
			ds_retorno_w := substr(obter_nome_pf(cd_medico_w),1,254);
		elsif (cd_cgc_indicacao_w IS NOT NULL AND cd_cgc_indicacao_w::text <> '') then 
			ds_retorno_w := substr(obter_nome_pf_pj(null, cd_cgc_indicacao_w),1,254);
		elsif (cd_tipo_midia_w IS NOT NULL AND cd_tipo_midia_w::text <> '') then 
			ds_retorno_w := substr(obter_desc_tipo_midia(cd_tipo_midia_w),1,254);
		end if;
	end if;
end if;	
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
