SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_pessoa_informacao (ds_informacao_p text, ie_tipo_informacao_p text) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;


BEGIN
if (coalesce(ds_informacao_p,'0') <> '0') then
	begin

	if (ie_tipo_informacao_p <> 'C') then
		begin
		if (ie_tipo_informacao_p = 'P') then /* Número do CPF */
			begin
			select	max(cd_pessoa_fisica)
			into STRICT	cd_pessoa_fisica_w
			from	pessoa_fisica
			where	nr_cpf = ds_informacao_p;
			end;
		elsif (ie_tipo_informacao_p = 'I') then /* Número da identidade */
			begin
			select	max(cd_pessoa_fisica)
			into STRICT	cd_pessoa_fisica_w
			from	pessoa_fisica
			where	nr_identidade = ds_informacao_p;
			end;
		elsif (ie_tipo_informacao_p = 'F') then /* Código do funcionário/matrícula */
			begin
			select	max(cd_pessoa_fisica)
			into STRICT	cd_pessoa_fisica_w
			from	pessoa_fisica
			where	cd_funcionario = ds_informacao_p;
			end;
		elsif (ie_tipo_informacao_p = 'X') then /* Número da identidade + Número do CPF*/
			begin
			select	max(cd_pessoa_fisica)
			into STRICT	cd_pessoa_fisica_w
			from	pessoa_fisica
			where	nr_cpf = ds_informacao_p
			or	nr_identidade = ds_informacao_p;
			end;
		elsif (ie_tipo_informacao_p = 'U') then /* Nome do usuário */
			begin
			select	cd_pessoa_fisica
			into STRICT	cd_pessoa_fisica_w
			from	usuario
			where	nm_usuario = ds_informacao_p;
			exception
			when others then
				cd_pessoa_fisica_w := null;
			end;
		end if;
		end;
	end if;

	if (coalesce(cd_pessoa_fisica_w,'0') = '0') then
		begin
		select	cd_pessoa_fisica
		into STRICT	cd_pessoa_fisica_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = ds_informacao_p;
		exception
		when others then
			cd_pessoa_fisica_w := null;
		end;
	end if;
	end;
end if;


return	cd_pessoa_fisica_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
