CREATE OR REPLACE FUNCTION obter_pessoa_movto_cr (nr_seq_movto_cr_p bigint, ie_tipo_pessoa_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(255);
cd_pessoa_movto_cr_w	varchar(10);
cd_cgc_movto_cr_w	varchar(14);
/*
1 - física
2 - Jurídica
*/
BEGIN

if (nr_seq_movto_cr_p IS NOT NULL AND nr_seq_movto_cr_p::text <> '') then
	begin
	select	a.cd_pessoa_fisica,
		a.cd_cgc
	into STRICT	cd_pessoa_movto_cr_w,
		cd_cgc_movto_cr_w
	from	movto_cartao_cr a
	where	a.nr_sequencia = nr_seq_movto_cr_p;
	exception
	when others then
		cd_pessoa_movto_cr_w	:= null;
		cd_cgc_movto_cr_w	:= null;
	end;
end if;

if (ie_tipo_pessoa_p = 1) then
	ds_retorno_w := cd_pessoa_movto_cr_w;
elsif (ie_tipo_pessoa_p = 2) then
	ds_retorno_w := cd_cgc_movto_cr_w;
end if;


if (coalesce(ds_retorno_w::text, '') = '')then
	begin
	select	a.cd_pessoa_fisica,
		a.cd_cgc
	into STRICT	cd_pessoa_movto_cr_w,
		cd_cgc_movto_cr_w
	from	caixa_receb a,
		movto_cartao_cr b
	where	a.nr_sequencia = b.nr_seq_caixa_rec
	and	b.nr_sequencia = nr_seq_movto_cr_p;
	exception
	when others then
		cd_pessoa_movto_cr_w := null;
		cd_cgc_movto_cr_w    := null;
	end;

	if (ie_tipo_pessoa_p = 1) then
		ds_retorno_w := cd_pessoa_movto_cr_w;
	elsif (ie_tipo_pessoa_p = 2) then
		ds_retorno_w := cd_cgc_movto_cr_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
