CREATE OR REPLACE FUNCTION obter_pessoa_tit_adiant ( nr_seq_caixa_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_atendimento_w	bigint;
ds_paciente_w		varchar(255);

C01 CURSOR FOR 
SELECT	distinct 
	nr_atendimento 
from ( 
	SELECT	a.nr_atendimento 
	from	titulo_receber a, 
		titulo_receber_liq b 
	where	a.nr_titulo = b.nr_titulo 
	and	b.nr_seq_caixa_rec = nr_seq_caixa_p 
	
union all
 
	select	a.nr_atendimento 
	from	adiantamento a 
	where	a.nr_seq_caixa_rec = nr_seq_caixa_p) alias0;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	if (coalesce(ds_paciente_w::text, '') = '') then 
		ds_paciente_w	:= substr(obter_nome_pf_atend(nr_atendimento_w),1,255);
	else 
		ds_paciente_w	:= substr(ds_paciente_w || ', ' || obter_nome_pf_atend(nr_atendimento_w),1,255);
	end if;
	 
	end;
end loop;
close C01;
 
return	ds_paciente_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
