CREATE OR REPLACE FUNCTION obter_pessoa_viagem_desp ( nr_seq_viagem_desp_p bigint, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE

	 
ds_retorno_w			varchar(80);
cd_pessoa_fisica_w		varchar(10);
nm_pessoa_fisica_w		varchar(80);


BEGIN 
 
begin 
select	b.cd_pessoa_fisica, 
	substr(obter_nome_pf(b.cd_pessoa_fisica),1,80) 
into STRICT	cd_pessoa_fisica_w, 
	nm_pessoa_fisica_w 
from	via_viagem	b, 
	via_relat_desp	a 
where	a.nr_seq_viagem	= b.nr_sequencia 
and	a.nr_sequencia	= nr_seq_viagem_desp_p;
exception 
	when others then 
	cd_pessoa_fisica_w	:= '';
	nm_pessoa_fisica_w	:= '';
end;
 
 
if (ie_tipo_retorno_p	= 'C') then 
	ds_retorno_w		:= cd_pessoa_fisica_w;
elsif (ie_tipo_retorno_p	= 'N') then 
	ds_retorno_w		:= nm_pessoa_fisica_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
