CREATE OR REPLACE FUNCTION obter_pe_prescr_proc_obs ( nr_sequencia_p bigint) RETURNS PE_PRESCR_PROC_OBS.DS_OBSERVACAO%TYPE AS $body$
DECLARE


  ds_retorno_w pe_prescr_proc_obs.ds_observacao%TYPE := null;

  cursor_obs CURSOR FOR
  SELECT ds_observacao
  from PE_PRESCR_PROC_OBS
  where NR_SEQ_PE_PROC = nr_sequencia_p
  and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
  and coalesce(dt_inativacao::text, '') = '';

 l_cursor_obs  cursor_obs%ROWTYPE;


BEGIN

 OPEN cursor_obs;
  LOOP
    FETCH cursor_obs INTO l_cursor_obs;
    EXIT WHEN NOT FOUND; /* apply on cursor_obs */
    ds_retorno_w := substr(ds_retorno_w || ',' || l_cursor_obs.ds_observacao, 1, 255);
  END LOOP;
    CLOSE cursor_obs;
  RETURN LTRIM(ds_retorno_w, ',');

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
