CREATE OR REPLACE FUNCTION obter_pflegegrad ( cd_pessoa_fisica_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_pflegegrad_w			varchar(255);
ie_erfolgter_w			varchar(1);


BEGIN

	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		select 	min(ie_pflegegrad),
				min(ie_erfolgter)
		into STRICT	ie_pflegegrad_w,
				ie_erfolgter_w
		from (	SELECT	ie_pflegegrad,
						ie_erfolgter
				from	paciente_pflegegrad
				where	cd_pessoa_fisica = cd_pessoa_fisica_p
				and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
				and		coalesce(dt_inativacao::text, '') = ''
				order by dt_liberacao desc) alias5 LIMIT 1;
	end if;

	if (coalesce(ie_pflegegrad_w::text, '') = '' or ie_erfolgter_w = 'S') then
		ie_pflegegrad_w := obter_desc_expressao(886693);
	end if;

	return ie_pflegegrad_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
