SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_pf_atendimento (NR_ATENDIMENTO_P bigint, IE_OPCAO_P text) RETURNS bigint AS $body$
DECLARE

  DS_RETORNO_W varchar(80) := '';

BEGIN
    IF (NR_ATENDIMENTO_P IS NOT NULL AND NR_ATENDIMENTO_P::text <> '') THEN
      IF ( IE_OPCAO_P = 'C' ) THEN
        BEGIN
            SELECT MAX(A.CD_PESSOA_FISICA)
            INTO STRICT   DS_RETORNO_W
            FROM   ATENDIMENTO_PACIENTE A
            WHERE  A.NR_ATENDIMENTO = NR_ATENDIMENTO_P;
        EXCEPTION
            WHEN OTHERS THEN
              DS_RETORNO_W := '';
        END;
      ELSE
        BEGIN
            SELECT SUBSTR(OBTER_NOME_PF(A.CD_PESSOA_FISICA), 1, 80)
            INTO STRICT   DS_RETORNO_W
            FROM   ATENDIMENTO_PACIENTE A
            WHERE  A.NR_ATENDIMENTO = NR_ATENDIMENTO_P;
        EXCEPTION
            WHEN OTHERS THEN
              DS_RETORNO_W := '';
        END;
      END IF;
    END IF;

    RETURN DS_RETORNO_W;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
