CREATE OR REPLACE FUNCTION obter_pf_destino_ci ( nm_usuario_destino_p text) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(255);
ds_lista_pf_w			varchar(4000);
nm_usuario_w			varchar(4000);
lista_w	dbms_sql.varchar2_table;

BEGIN

if (nm_usuario_destino_p IS NOT NULL AND nm_usuario_destino_p::text <> '') then
	begin
	lista_w	:= obter_lista_string(substr(nm_usuario_destino_p, 1, 2000),',');
	exception
	when others then
		null;
	end;
	for i in 1..lista_w.count loop
		begin
		
		nm_usuario_w	:= trim(both lista_w(i));
		
		select	max(cd_pessoa_fisica)
		into STRICT	cd_pessoa_fisica_w
		from	usuario
		where	nm_usuario	= nm_usuario_w;
		
		if (coalesce(ds_lista_pf_w::text, '') = '') then
			ds_lista_pf_w	:= substr(obter_nome_pf(cd_pessoa_fisica_w),1,4000);
		else
			ds_lista_pf_w	:= substr(ds_lista_pf_w || ', ' || obter_nome_pf(cd_pessoa_fisica_w), 1,4000);
		end if;
		
		end;
	end loop;
	
end if;

return	ds_lista_pf_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
