CREATE OR REPLACE FUNCTION obter_pf_destino (nm_usuario_destino_p text) RETURNS varchar AS $body$
DECLARE

 
cd_pessoa_fisica_w		varchar(255);
nm_usuario_destino_w		varchar(4000);
ds_lista_pf_w			varchar(4000);

c01 CURSOR FOR 
SELECT	cd_pessoa_fisica 
from	usuario 
where	nm_usuario_destino_w like '%  ' || nm_usuario || '  %' 
and	(trim(both nm_usuario_destino_w) IS NOT NULL AND (trim(both nm_usuario_destino_w))::text <> '');


BEGIN 
 
nm_usuario_destino_w	:= substr('  ' || replace(nm_usuario_destino_p, ',', '  ') || '  ',1,4000);
 
open c01;
loop 
fetch c01 into 
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (coalesce(ds_lista_pf_w::text, '') = '') then 
		ds_lista_pf_w	:= substr(obter_nome_pf(cd_pessoa_fisica_w),1,4000);
	else 
		ds_lista_pf_w	:= substr(ds_lista_pf_w || ', ' || obter_nome_pf(cd_pessoa_fisica_w), 1,4000);
	end if;
end loop;
close c01;
 
return	ds_lista_pf_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
