SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_pf_pront (nr_prontuario_p bigint, ie_opcao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);
ie_regra_pront_w	varchar(15);
cd_pessoa_fisica_w	varchar(10);
nm_pessoa_fisica_w	varchar(255);


BEGIN 
if (nr_prontuario_p IS NOT NULL AND nr_prontuario_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then 
	 
	ie_regra_pront_w := obter_param_usuario(0, 120, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_regra_pront_w);
	 
	 
	if (ie_regra_pront_w = 'BASE') or (ie_regra_pront_w = 'NUNCA') then 
 
		select	max(cd_pessoa_fisica), 
			max(substr(obter_nome_pf(cd_pessoa_fisica),1,255)) 
		into STRICT	cd_pessoa_fisica_w, 
			nm_pessoa_fisica_w 
		from	pessoa_fisica 
		where	nr_prontuario	= nr_prontuario_p;
		 
	elsif (ie_regra_pront_w = 'ESTAB') then 
 
		select	max(b.cd_pessoa_fisica), 
			max(substr(obter_nome_pf(b.cd_pessoa_fisica),1,255)) 
		into STRICT	cd_pessoa_fisica_w, 
			nm_pessoa_fisica_w		 
		from	pessoa_fisica_pront_estab a,	 
			pessoa_fisica b 
		where	a.cd_pessoa_fisica 	= b.cd_pessoa_fisica 
		and	a.nr_prontuario		= nr_prontuario_p 
		and	a.cd_estabelecimento	= cd_estabelecimento_p;
		 
	end if;
	 
	if (ie_opcao_p = 'C') then 
		ds_retorno_w := cd_pessoa_fisica_w;
	elsif (ie_opcao_p = 'D') then 
		ds_retorno_w := nm_pessoa_fisica_w;
	end if;
	 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
