CREATE OR REPLACE FUNCTION obter_pf_usuario_assinatura ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_itens_w		bigint;
nm_usuario_original_w	varchar(15);
nm_usuario_w		varchar(15);
qt_conta_w		bigint := 0;
cd_pessoa_usuario_w	varchar(10);

C01 CURSOR FOR
	SELECT	sum(qt_itens) qt_itens,
		nm_usuario_original
	from (
		SELECT	count(*) qt_itens,
			nm_usuario_original
		from	material_atend_paciente
		where	nr_atendimento = nr_atendimento_p
		and (cd_setor_atendimento = cd_setor_atendimento_p or cd_setor_atendimento_p = 0)
		and	(dt_autenticacao IS NOT NULL AND dt_autenticacao::text <> '')
		group by	nm_usuario_original
		
union all

		select	count(*) qt_itens,
			nm_usuario_original
		from	procedimento_paciente
		where	nr_atendimento = nr_atendimento_p
		and (cd_setor_atendimento = cd_setor_atendimento_p or cd_setor_atendimento_p = 0)
		and	(dt_autenticacao IS NOT NULL AND dt_autenticacao::text <> '')
		group by	nm_usuario_original) alias7
	group by nm_usuario_original;


BEGIN

open C01;
loop
fetch C01 into
	qt_itens_w,
	nm_usuario_original_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (qt_itens_w > qt_conta_w) then
		nm_usuario_w := nm_usuario_original_w;
	end if;
	qt_conta_w := qt_itens_w;

	end;
end loop;
close C01;

select 	coalesce(max(cd_pessoa_fisica),'0')
into STRICT	cd_pessoa_usuario_w
from 	usuario
where 	nm_usuario = nm_usuario_w;

return	cd_pessoa_usuario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
