CREATE OR REPLACE FUNCTION obter_pf_usuario (nm_usuario_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(100) := '';


BEGIN


if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	if (ie_opcao_p = 'C') then
		begin
		select	a.cd_pessoa_fisica
		into STRICT	ds_retorno_w
		from	usuario a
		where	a.nm_usuario_pesquisa = upper(nm_usuario_p);
		exception
			when others then
				ds_retorno_w	:= '';
		end;
	else
		begin
		select	substr(obter_nome_pf(b.cd_pessoa_fisica),1,100)
		into STRICT	ds_retorno_w
		from	pessoa_fisica b,
			usuario a
		where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
		and	a.nm_usuario_pesquisa = upper(nm_usuario_p);
		exception
			when others then
				ds_retorno_w	:= '';
		end;
	end	if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
