CREATE OR REPLACE FUNCTION obter_pj_empresa_ref ( CD_EMPRESA_P bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(14);


BEGIN
if (cd_empresa_p IS NOT NULL AND cd_empresa_p::text <> '') then
	begin
	select	cd_cgc
	into STRICT	ds_retorno_w
	from	empresa_referencia
	where	cd_empresa	= cd_empresa_p;
