SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_pj_pf_se_idade ( cd_pessoa_fisica_p text, cd_cgc_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		pessoa_juridica.ds_razao_social%type;
dt_nascimento_w		varchar(80);
nr_anos_w		bigint;


BEGIN
ds_retorno_w		:= '';

select obter_dados_pf(cd_pessoa_fisica_p,'dn')
into STRICT dt_nascimento_w
;

select obter_idade(dt_nascimento_w,clock_timestamp(),'a')
into STRICT nr_anos_w
;

if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
	begin
	select ds_razao_social
	into STRICT	 ds_retorno_w
	from	 pessoa_juridica
	where	 cd_cgc	= cd_cgc_p;
	end;
elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
		if (nr_anos_w < 18) then
			select obter_compl_pf(cd_pessoa_fisica_p,3,'n')
			into STRICT   ds_retorno_w
			;
		else
			select 	 substr(obter_nome_pf(cd_pessoa_fisica),1,80)
			into STRICT	 ds_retorno_w
			from	 pessoa_fisica
			where	 cd_pessoa_fisica	= cd_pessoa_fisica_p;
		end if;
	end;

end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
