CREATE OR REPLACE FUNCTION obter_pj_titulo_pagar (nr_titulo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
cd_cgc_w	varchar(14);
ds_retorno_w	varchar(80);


BEGIN 
 
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then 
	select	cd_cgc 
	into STRICT	cd_cgc_w 
	from	titulo_pagar 
	where	nr_titulo	= nr_titulo_p;
 
	if (coalesce(ie_opcao_p,'C') = 'D') then 
		select	obter_nome_pf_pj(null, cd_cgc_w) 
		into STRICT	ds_retorno_w 
		;
	else 
		ds_retorno_w	:= cd_cgc_w;
	end if;
end if;
 
RETURN	ds_retorno_w;
 
END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
