CREATE OR REPLACE FUNCTION obter_plano_atendimento ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_convenio_w			bigint;
cd_plano_w			varchar(100);
ds_retorno_w			varchar(200);
ds_plano_w			varchar(200);



BEGIN

select coalesce(max(cd_convenio),0),
	coalesce(max(cd_plano_convenio),0)
into STRICT 	cd_convenio_w,
	cd_plano_w
from 	Atend_categoria_convenio a
where 	a.nr_atendimento	= nr_atendimento_p
and 	a.dt_inicio_vigencia	= (	SELECT 	max(dt_inicio_vigencia)
					from 	Atend_categoria_convenio b
					where nr_atendimento	= nr_atendimento_p);


select	coalesce(max(ds_plano),'')-- Richart tratei para passar '' caso o mesmo n√£o encontre o plano, pois esta gerando erro na base do teste.
into STRICT	ds_plano_w
from	convenio_plano
where	cd_convenio		= cd_convenio_w
and	cd_plano		= cd_plano_w;

if (ie_opcao_p	= 'C') then
	ds_retorno_w	:= cd_plano_w;
elsif (ie_opcao_p	= 'D') then
	ds_retorno_w	:= ds_plano_w;
elsif (ie_opcao_p	= 'CC') then
	ds_retorno_w	:= cd_convenio_w ||cd_plano_w;
end if;


RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
