CREATE OR REPLACE FUNCTION obter_plano_usuario_convenio ( cd_convenio_p bigint, cd_categoria_p text, cd_usuario_convenio_p text) RETURNS varchar AS $body$
DECLARE


cd_plano_w		varchar(10);


BEGIN
if (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') and (cd_categoria_p IS NOT NULL AND cd_categoria_p::text <> '') and (cd_usuario_convenio_p IS NOT NULL AND cd_usuario_convenio_p::text <> '') then
	begin
	select	max(cd_plano_padrao)
	into STRICT	cd_plano_w
	from 	categoria_convenio
	where	cd_convenio = cd_convenio_p
	and	cd_categoria = cd_categoria_p;

	if (coalesce(cd_plano_w::text, '') = '') or (cd_plano_w = '') then
		begin
		select	obter_plano_cod_usuario_conv(cd_convenio_p, cd_usuario_convenio_p)
		into STRICT 	cd_plano_w
		;
		end;
	end if;
	end;
end if;
return cd_plano_w;
end;
