SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_plataforma_news ( nr_seq_alteracao_p alteracao_tasy.nr_sequencia%type ) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [ X ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção: 
-------------------------------------------------------------------------------------------------------------------

Referências: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(4000) := null;


BEGIN


if (phi_is_base_philips = 'S') then
	begin
		
		select	CASE WHEN ie_delphi='S' THEN  ' Delphi, ' END
			|| CASE WHEN ie_java='S' THEN  ' Java, ' END 
			|| CASE WHEN ie_html5='S' THEN  ' HTML5, ' END 
			|| CASE WHEN ie_tws='S' THEN  ' TWS, ' END 
		into STRICT	ds_retorno_w
		from	alteracao_tasy
		where	nr_sequencia = nr_seq_alteracao_p;

		ds_retorno_w := substr(ds_retorno_w, 1, length(ds_retorno_w) -2);
	end;
end if;

	return ds_retorno_w;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
