CREATE OR REPLACE FUNCTION obter_ponto_proc_pac ( nr_sequencia_p bigint, ie_ponto_p text) RETURNS bigint AS $body$
DECLARE

 
qt_ponto_w		double precision;


BEGIN 
 
begin 
select	coalesce(f.vl_procedimento,0) 
into STRICT	qt_ponto_w 
from	proc_paciente_valor f, 
	procedimento_paciente p 
where	f.nr_seq_procedimento 	= p.nr_sequencia 
and	p.nr_sequencia 		= nr_sequencia_p 
and	f.ie_tipo_valor		= ie_ponto_p;
exception 
	when others then 
		qt_ponto_w	:= null;
end;
 
return qt_ponto_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
