CREATE OR REPLACE FUNCTION obter_ponto_sadt ( nr_seq_proc_p bigint) RETURNS bigint AS $body$
DECLARE

			 
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
dt_procedimento_w	timestamp;
qt_ponto_w		smallint	:= 0;
qt_procedimento_w	double precision	:= 0;
cd_estabelecimento_w	smallint;
cd_convenio_w		integer;

BEGIN
 
select	a.cd_procedimento, 
	a.ie_origem_proced, 
	a.dt_procedimento, 
	a.qt_procedimento, 
	b.cd_estabelecimento, 
	b.cd_convenio_parametro 
into STRICT	cd_procedimento_w, 
	ie_origem_proced_w, 
	dt_procedimento_w, 
	qt_procedimento_w, 
	cd_estabelecimento_w, 
	cd_convenio_w 
from	procedimento_paciente a, 
	conta_paciente b 
where	a.nr_interno_conta	= b.nr_interno_conta 
and	nr_sequencia		= nr_seq_proc_p;
 
begin 
select	coalesce(qt_ponto,0) 
into STRICT	qt_ponto_w 
from	convenio_sadt 
where	cd_procedimento		= cd_procedimento_w 
and	ie_origem_proced	= ie_origem_proced_w 
and	cd_convenio		= cd_convenio_w 
and	dt_procedimento_w between dt_inicio_vigencia and coalesce(dt_final_vigencia,dt_procedimento_w) 
and	qt_ponto		> 0;
exception 
	when no_data_found then 
		qt_ponto_w	:= 0;
	when others then 
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(279928, 'CD_PROCEDIMENTO_P=' || cd_procedimento_w);
end;
 
return(qt_ponto_w * qt_procedimento_w);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
