SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_pontuacao_charlson ( nr_atendimento_p bigint, ie_pontuacao_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_pontuacao_p
P = Pontuação
I = Pontuação ponderada por idade
*/
nr_seq_escala_w	bigint;
qt_pontuacao_w	smallint;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_escala_w
	from	escala_charlson a
	where	a.nr_atendimento = nr_atendimento_p
	and	a.ie_situacao = 'A';

	if (coalesce(nr_seq_escala_w,0) > 0) then
		if (ie_pontuacao_p = 'I') then
			select	a.qt_pontuacao_idade
			into STRICT	qt_pontuacao_w
			from	escala_charlson a
			where	a.nr_sequencia = nr_seq_escala_w;
		else
			select	a.qt_pontuacao
			into STRICT	qt_pontuacao_w
			from	escala_charlson a
			where	a.nr_sequencia = nr_seq_escala_w;
		end if;
	end if;
end if;

return	qt_pontuacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
