SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_pontuacao_mews ( nr_atendimento_p bigint, qt_horas_cons_p bigint, ie_opcao_p text default null) RETURNS varchar AS $body$
DECLARE

			
qt_pontuacao_w		integer;	
ie_prioritario_w	varchar(10);
nr_sequencia_w 		bigint;
nr_sequencia_w2     bigint;


BEGIN

select max(b.nr_sequencia)
into STRICT nr_sequencia_w
			  from 	 escala_mews b
			  where	 nr_atendimento = nr_atendimento_p
			  and 	 coalesce(b.dt_inativacao::text, '') = ''
			  and	 (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '');
			
select max(b.nr_sequencia)
into STRICT nr_sequencia_w2
            from 	 escala_mews b
            where	 nr_atendimento = nr_atendimento_p
            and 	 coalesce(b.dt_inativacao::text, '') = ''
            and	 (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
            and	 ((qt_horas_cons_p = 0) or (b.dt_avaliacao  between (clock_timestamp() - (qt_horas_cons_p / 24)) and clock_timestamp()));
	
select 	coalesce(max(a.qt_pontuacao),0)
into STRICT	qt_pontuacao_w
from	escala_mews a
where	a.nr_atendimento = nr_atendimento_p
and	a.nr_sequencia = nr_sequencia_w2;	

if (qt_pontuacao_w >= 3) then
	ie_prioritario_w := 'S';

elsif (coalesce(nr_sequencia_w::text, '') = '') then
qt_pontuacao_w := null;	
	
end if;

if (coalesce(ie_opcao_p,'P') = 'P') then
	return	qt_pontuacao_w;
elsif (ie_opcao_p = 'C') then
	return ie_prioritario_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
