CREATE OR REPLACE FUNCTION obter_pontuacao_sentry_score ( nr_atendimento_p bigint, vl_rownum_p bigint) RETURNS bigint AS $body$
DECLARE


ds_return_w		smallint := 0;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '' AND vl_rownum_p IS NOT NULL AND vl_rownum_p::text <> '') then

	begin

			select		nr_pontuacao
			into STRICT		ds_return_w
			from (
						SELECT	row_number() OVER () AS RN, nr_pontuacao
						from (
								SELECT		nr_pontuacao
								from		sentry_score
								where		nr_atendimento = nr_atendimento_p
								order by	dt_pontuacao desc, nr_sequencia desc) alias1 LIMIT 2) alias2
			where		RN = vl_rownum_p;
			
	EXCEPTION WHEN no_data_found then

			ds_return_w := null;

	end;

end if;

return ds_return_w;

end;
$body$
