SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_pont_etapa_sup ( nr_seq_regra_sup_p bigint) RETURNS varchar AS $body$
DECLARE

  ds_retorno_w 					varchar(255) := '';
  ds_expressao_w				varchar(255) := '';
  qt_ponto_sim_w				gqa_acao.qt_ponto_sim%type;		
  qt_ponto_nao_w				gqa_acao.qt_ponto_nao%type;
  qt_ponto_cancelar_w			gqa_acao.qt_ponto_cancelar%type;
  qt_ponto_confirma_w			gqa_acao.qt_ponto_confirma%type;
  ie_tipo_acao_w				gqa_acao.ie_tipo_acao%type;


BEGIN
  if (nr_seq_regra_sup_p IS NOT NULL AND nr_seq_regra_sup_p::text <> '') then
    
		select  max(b.qt_ponto_cancelar),
				max(b.qt_ponto_confirma),
				max(b.qt_ponto_sim),
				max(b.qt_ponto_nao),
				max(b.ie_tipo_acao)
		into STRICT	qt_ponto_cancelar_w,
				qt_ponto_confirma_w,
				qt_ponto_sim_w,
				qt_ponto_nao_w,
				ie_tipo_acao_w
		from 	gqa_pendencia_regra a,
				gqa_acao b
		where 	b.nr_seq_pend_regra = a.nr_sequencia
		and		a.nr_sequencia = nr_seq_regra_sup_p;
		
		if ( ie_tipo_acao_w = 'ME' ) then
		
		
			if (qt_ponto_cancelar_w IS NOT NULL AND qt_ponto_cancelar_w::text <> '') then
		
			Select  obter_desc_expressao(302745)
			into STRICT	ds_expressao_w
			;
		
				ds_retorno_w := ds_expressao_w||'( '||qt_ponto_cancelar_w||' )  ';		
				
			end if;
			
			if (qt_ponto_confirma_w IS NOT NULL AND qt_ponto_confirma_w::text <> '') then
			
				Select  obter_desc_expressao(321624)
				into STRICT	ds_expressao_w
				;
			
				ds_retorno_w := substr(ds_retorno_w|| ds_expressao_w||'( '||qt_ponto_confirma_w||' )  ',1,255);		

			end if;
		
		
		elsif ( ie_tipo_acao_w = 'PE' or ie_tipo_acao_w = 'PG' or ie_tipo_acao_w = 'DL') then
		
					
			if (qt_ponto_nao_w IS NOT NULL AND qt_ponto_nao_w::text <> '') then
			
				Select  obter_desc_expressao(327114)
				into STRICT	ds_expressao_w
				;
			
				ds_retorno_w := substr(ds_retorno_w|| ds_expressao_w||'( '||qt_ponto_nao_w||' )  ',1,255);		

			end if;	
			
			if (qt_ponto_sim_w IS NOT NULL AND qt_ponto_sim_w::text <> '') then
			
				Select  obter_desc_expressao(719927)||'/'||obter_desc_expressao(486410)
				into STRICT	ds_expressao_w
				;
			
				ds_retorno_w := substr(ds_retorno_w|| ds_expressao_w||'( '||qt_ponto_sim_w||' )  ',1,255);		

			end if;	
			
		END IF;
		

  end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
