CREATE OR REPLACE FUNCTION obter_pont_feq_resp_prism_md ( qt_freq_resp_p bigint, ie_apneia_p text, ie_lactante_p text ) RETURNS bigint AS $body$
DECLARE

    qt_pto_fr_w smallint := 0;

BEGIN
    IF ( coalesce(qt_freq_resp_p::text, '') = '' )
        AND ( ie_apneia_p = 'N' )
    THEN
        qt_pto_fr_w := 0;
    ELSIF ( ie_apneia_p = 'S' ) THEN
        qt_pto_fr_w := 5;
    ELSIF ( ie_lactante_p = 'S' )
        AND ( qt_freq_resp_p >= 61 )
        AND ( qt_freq_resp_p <= 90 )
    THEN
        qt_pto_fr_w := 1;
    ELSIF ( ie_lactante_p = 'S' )
        AND ( qt_freq_resp_p > 90 )
    THEN
        qt_pto_fr_w := 5;
    ELSIF ( ie_lactante_p = 'N' )
        AND ( qt_freq_resp_p >= 51 )
        AND ( qt_freq_resp_p <= 70 )
    THEN
        qt_pto_fr_w := 1;
    ELSIF ( ie_lactante_p = 'N' )
        AND ( qt_freq_resp_p > 70 )
    THEN
        qt_pto_fr_w := 5;
    ELSE
        qt_pto_fr_w := 0;
    END IF;

    RETURN qt_pto_fr_w;
END;
$body$
