CREATE OR REPLACE FUNCTION obter_pont_helios_kliniken_md ( ie_risco1_p text, ie_risco2_p text ) RETURNS bigint AS $body$
DECLARE

    qt_pontuacao_w bigint;
    qt_risco1_w    bigint;
    qt_risco2_w    bigint;

BEGIN
    IF ( ie_risco1_p = 'RH1' OR ie_risco1_p = 'RH2' ) THEN
        qt_risco1_w := 5;
    ELSIF ( ie_risco1_p = 'RM1' OR ie_risco1_p = 'RM2' ) THEN
        qt_risco1_w := 3;
    ELSIF ( ie_risco1_p = 'RG2' ) THEN
        qt_risco1_w := 2;
    ELSIF ( ie_risco1_p = 'RG1' ) THEN
        qt_risco1_w := 1;
    ELSE
        qt_risco1_w := 0;
    END IF;

    IF ( ie_risco2_p = 'RH1' OR ie_risco2_p = 'RH2' ) THEN
        qt_risco2_w := 5;
    ELSIF ( ie_risco2_p = 'RM1' OR ie_risco2_p = 'RM2' ) THEN
        qt_risco2_w := 3;
    ELSIF ( ie_risco2_p = 'RG2' ) THEN
        qt_risco2_w := 2;
    ELSIF ( ie_risco2_p = 'RG1' ) THEN
        qt_risco2_w := 1;
    ELSE
        qt_risco2_w := 0;
    END IF;

    qt_pontuacao_w := coalesce(qt_risco1_w, 0) + coalesce(qt_risco2_w, 0);

    RETURN qt_pontuacao_w;

END;
