CREATE OR REPLACE FUNCTION obter_porcentagem_upp (dt_inicio_p timestamp, dt_fim_p timestamp, ie_tipo_p text default 'CS') RETURNS bigint AS $body$
DECLARE

 
qt_reg_w 	bigint;
qt_reg_total_w 	bigint;

/* 
CS - Com registro de SAE 
SS - Sem registro de SAE 
*/
 
 

BEGIN 
 
select 	count(distinct nr_atendimento) 
into STRICT	qt_reg_w 
from 	eis_risco_upp_v 
where 	dt_liberacao between dt_inicio_p and fim_dia(dt_fim_p) 
and 	ie_sae = ie_tipo_p;
 
select 	count(distinct nr_atendimento) 
into STRICT	qt_reg_total_w 
from 	eis_risco_upp_v 
where 	dt_liberacao between dt_inicio_p and fim_dia(dt_fim_p);
 
if (qt_reg_total_w = 0) then 
	qt_reg_total_w := 1;
end if;
 
return(qt_reg_w * 100) / qt_reg_total_w;
 
end;
